window.onload=function(){var e=document.getElementsByClassName("scrollup")[0];window.onscroll=()=>{500<window.scrollY?e.classList.add("d-block"):e.classList.remove("d-block")},e.addEventListener("click",function(){window.scrollTo({top:0,behavior:"smooth"}),setTimeout(function(){history.pushState("",document.title,window.location.pathname+window.location.search)})})};